//originファイルはモジュール名だけを宣言し、各モジュールは別ファイルとして管理する
angular.module('ArticleApplication', ['ui.router']).config(function ($stateProvider) {
    $stateProvider.state('top', {
        url: '/',
        templateUrl: 'templates/contents.html',
        controller: 'ArticleController',
        controllerAs: 'article'
    });
});
angular.module('ArticleApplication').controller('ArticleController', ['MockHeaders', function (MockHeaders) {

    this.message = 'Hello,Module-resolved angularJs!';
    this.headers = MockHeaders();
}]);
angular.module('ArticleApplication').directive('myHello', function () {
    return {
        restrict: 'E',
        template: '<h1>Hello,MyDirective(This is resolved by angular-di)!</h1>'
    };
});

angular.module('ArticleApplication').directive('myHoge', function () {
    return {
        restrict: 'E',
        templateUrl: 'contents.html'
    };
});
angular.module('ArticleApplication').value('MockHeaders', function () {
    var ret = [];
    ret.push({
        id: 1,
        title: 'hoge'
    });
    ret.push({
        id: 2,
        title: 'foo'
    });
    return ret;
});